let signature=customRef((track,trigger)=>{
    return{
        // get何时被调用---msg被读取时
        get(){
            track()//告诉Vue数据msg很重要，要对msg持续关注，一旦msg变化就更新
            console.log('get');
            return user.signature
        },
        // set何时被调用----msg被修改时
        set(val){
            trigger()//通知Vue数据msg变化了
            console.log('set',val);
            user.signature=val
        }
    }
})
